import React, { useEffect, useState } from 'react'
import Header from '../Header/Header'
import axios from 'axios';
import ApiEndPoint from '../ApiEndPoint/ApiEndPoint';

function Recptionlist() {
  const inititalState = {
    receptionList: null
}    
const reducer = (state,action)=>{
  if(action.type=="set-reception-list"){
    return {...state,receptionList:action.payload}
  }
  else if(action.type=="delete-reception"){
    let index = state.receptionList.findIndex((obj)=>obj.id == action.payload);
    state.receptionList.splice(index,1);
    return {...state};
  }
}
const [data,dispatch] = useReducer(reducer,inititalState);
useEffect(()=>{
    loadReceptionList();
},[]);

const loadReceptionList = async ()=>{
   try{ 
    let token = sessionStorage.getItem("token");
    let response = await axios.get(apis.receptionList,{
        headers: { Authorization: `Bearer ${token}` }
    });
    if(response.data.status){
      dispatch({type:"set-reception-list",payload:response.data.data});
    }
    else
      toast.error("Oops! something went wrong...");
   }
   catch(err){
    console.log(err);
   } 
}
  
  return (
    <>
      <Header />
      <div className="container mt-2">
        <table className="table">
          <thead>
            <tr>
              <th>S No.</th>
              <th>Id</th>
              <th>Name</th>
              <th>Contact</th>
              <th>Password</th>
              <th>Address</th>
              <th>Operation</th>
            </tr>
          </thead>
          <tbody>
            {recptionistData.map((reception, index) => 
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{reception.id}</td>
                <td>{reception.name}</td>
                <td><b>{reception.phoneNumber}</b><br/><i>{reception.email}</i></td>
                <td>{reception.password}</td>
                <td>{reception.raddress}</td>
                <td>
                  <button className="btn btn-dark">Delete</button>
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </>
  );
}

export default Recptionlist
