import React, { useEffect, useState } from 'react'
import Header from '../../Header/Header'
import Footer from '../../Footer/Footer'
import axios from 'axios';

function ApointmentList() {

    const [ApointmentList,setAppointmentList] = useState([]);

    const loadApointmentList = async () =>{
        try {
            let token = sessionStorage.getItem("token")
            let currentUser = sessionStorage.getItem("current_user");
            if(currentUser.type === "doctor"){
                let response = await axios.get(ApiEndpoint.apointmentListDoctor,{
                    headers:{
                        Authorization: "Bearer"+token
                    }
                })
            }else{
                let response = await axios.get(ApiEndpoint.apointmentListReception,{
                    headers:{
                        Authorization: "Bearer"+token
                    }
                })
            }

            if(response.data.status){
                setAppointmentList(response.data.data)
            }
        } catch (err) {
            console.log(err)
        }
    }
    useEffect(() => {
        loadApointmentList()
    }, []);

  return <>
   <Header/>
    {ApointmentList?.map(apointment)}
   <Footer/>
  </>
}

export default ApointmentList
